import React from 'react';
import { ArrowLeft, Calendar, TrendingUp, TrendingDown, Activity, List } from 'lucide-react';
import { getYearlyData } from './data';

const StatCard = ({ icon, label, value, subValue, comparison, backgroundColor }) => (
  <div style={{
    backgroundColor: backgroundColor,
    borderRadius: '12px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    padding: '24px',
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '24px',
  }}>
    <div style={{ display: 'flex', alignItems: 'center', marginBottom: '16px' }}>
      {React.cloneElement(icon, { size: 24, color: '#4B5563' })}
      <span style={{ marginLeft: '12px', fontSize: '18px', fontWeight: '500', color: '#4B5563' }}>{label}</span>
    </div>
    <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px' }}>{value}</div>
    {subValue && <div style={{ fontSize: '14px', color: '#6B7280' }}>{subValue}</div>}
    {comparison && (
      <div style={{ marginTop: '8px', fontSize: '14px' }}>
        <div style={{ color: comparison === 'N/A' ? 'gray' : (parseFloat(comparison) > 0 ? 'green' : 'red') }}>
          {comparison} rispetto all'anno precedente
        </div>
      </div>
    )}
  </div>
);

const calculateComparison = (current, previous) => {
  if (typeof current !== 'number' || typeof previous !== 'number') {
    return 'N/A';
  }
  const diff = current - previous;
  const percentage = (diff / previous) * 100;
  return percentage > 0 ? `+${percentage.toFixed(1)}%` : `${percentage.toFixed(1)}%`;
};

const processYearlyData = (data) => {
  if (!data) return null;

  const months = Object.keys(data);
  const totalTurni = months.reduce((sum, month) => sum + data[month].totaleTurni, 0);

  const monthWithMostTurni = months.reduce((max, month) => 
    data[month].totaleTurni > data[max].totaleTurni ? month : max, months[0]);

  const monthWithLeastTurni = months.reduce((min, month) => 
    data[month].totaleTurni < data[min].totaleTurni ? month : min, months[0]);

  return {
    totalTurni,
    avgTurni: totalTurni / months.length,
    monthWithMostTurni: {
      month: parseInt(monthWithMostTurni) + 1,
      value: data[monthWithMostTurni].totaleTurni
    },
    monthWithLeastTurni: {
      month: parseInt(monthWithLeastTurni) + 1,
      value: data[monthWithLeastTurni].totaleTurni
    }
  };
};

const MonthlyListCard = ({ data, backgroundColor }) => (
  <div style={{
    backgroundColor: backgroundColor,
    borderRadius: '12px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    padding: '24px',
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '24px',
  }}>
    <div style={{ display: 'flex', alignItems: 'center', marginBottom: '16px' }}>
      <List size={24} color='#4B5563' />
      <span style={{ marginLeft: '12px', fontSize: '18px', fontWeight: '500', color: '#4B5563' }}>Lista Mensile dei Turni</span>
    </div>
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      {Object.entries(data).map(([month, { totaleTurni }]) => (
        <div key={month} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
          <span style={{ color: '#4B5563' }}>{monthNames[parseInt(month)]}</span>
          <span style={{ fontWeight: 'bold', color: '#1F2937' }}>{totaleTurni}</span>
        </div>
      ))}
    </div>
  </div>
);

const LastYearView = ({ setView }) => {
  const currentYear = new Date().getFullYear();
  const yearlyData = getYearlyData(currentYear);
  const lastYearData = processYearlyData(yearlyData);
  const previousYearData = processYearlyData(getYearlyData(currentYear - 1));

  if (!lastYearData || !previousYearData) {
    return <div>Error loading data</div>;
  }

  const monthNames = ['Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno', 'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'];

  const stats = [
    { 
      icon: <Calendar />, 
      label: 'Totale Turni Annuali', 
      value: lastYearData.totalTurni,
      comparison: calculateComparison(lastYearData.totalTurni, previousYearData.totalTurni),
      backgroundColor: '#E6F3FF'
    },
    { 
      icon: <Activity />, 
      label: 'Media Turni Mensile', 
      value: lastYearData.avgTurni.toFixed(1),
      comparison: calculateComparison(lastYearData.avgTurni, previousYearData.avgTurni),
      backgroundColor: '#FFF0E6'
    },
    {
      component: MonthlyListCard,
      data: yearlyData,
      backgroundColor: '#E6FFE6'
    },
    { 
      icon: <TrendingUp />, 
      label: 'Mese con Più Turni', 
      value: `${lastYearData.monthWithMostTurni.value} (${monthNames[lastYearData.monthWithMostTurni.month - 1]})`,
      comparison: calculateComparison(lastYearData.monthWithMostTurni.value, previousYearData.monthWithMostTurni.value),
      backgroundColor: '#FFF0F0'
    },
    { 
      icon: <TrendingDown />, 
      label: 'Mese con Meno Turni', 
      value: `${lastYearData.monthWithLeastTurni.value} (${monthNames[lastYearData.monthWithLeastTurni.month - 1]})`,
      comparison: calculateComparison(lastYearData.monthWithLeastTurni.value, previousYearData.monthWithLeastTurni.value),
      backgroundColor: '#F0F0FF'
    },
  ];

  return (
    <div style={{
      backgroundColor: '#F0F9FF',
      minHeight: '100vh',
      padding: '24px',
      fontFamily: "'Helvetica Neue', Arial, sans-serif",
    }}>
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        {/* ... Il resto del codice rimane invariato ... */}

        <div>
          {stats.map((stat, index) => (
            stat.component ? 
              <stat.component key={index} {...stat} /> :
              <StatCard key={index} {...stat} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default LastYearView;
