import React, { useState, useEffect } from 'react';
import { MonthYearIcon, YearIcon, CompareMonthsIcon, CompareYearsIcon, PerformanceTrendIcon, InformationIcon, StatisticheFoniciIcon } from './Icons';
import { PlusCircle } from 'lucide-react';
import DashboardCard from './DashboardCard';
import StatisticheFonici from './StatisticheFonici';

const MainView = () => {
  const [visibleCards, setVisibleCards] = useState([]);
  const [currentView, setCurrentView] = useState('main');

  const cards = [
    { icon: <MonthYearIcon />, label: "Ultimo Mese", color: "#E6F3FF", onClick: () => setCurrentView('lastMonth') },
    { icon: <YearIcon />, label: "Ultimo Anno", color: "#FEF3C7", onClick: () => setCurrentView('lastYear') },
    { icon: <CompareMonthsIcon />, label: "Confronta mesi", color: "#D1FAE5", onClick: () => setCurrentView('compareMonths') },
    { icon: <CompareYearsIcon />, label: "Confronta Anno", color: "#EDE9FE", onClick: () => setCurrentView('compareYears') },
    { icon: <StatisticheFoniciIcon />, label: "Statistiche Fonici", color: "#FEE2E2", onClick: () => setCurrentView('statisticheFonici') },
    { icon: <PerformanceTrendIcon />, label: "Performance Trend", color: "#D1FAE5", onClick: () => setCurrentView('performanceTrend') },
    { icon: <InformationIcon />, label: "Informazioni", color: "#E0E7FF", onClick: () => setCurrentView('information') },
    { icon: <PlusCircle size={48} />, label: "Inserisci Dati", color: "#FDE68A", onClick: () => setCurrentView('dataInput') },
  ];

  useEffect(() => {
    const totalAnimationTime = 1000; // 1 second
    const intervalTime = totalAnimationTime / cards.length;

    cards.forEach((_, index) => {
      setTimeout(() => {
        setVisibleCards(prev => {
          const newIndexes = [...prev, index];
          return newIndexes.sort(() => Math.random() - 0.5);
        });
      }, index * intervalTime);
    });
  }, []);

  if (currentView === 'statisticheFonici') {
    return <StatisticheFonici onBack={() => setCurrentView('main')} />;
  }

  return (
    <div className="bg-blue-50 min-h-screen p-4 font-sans">
      <div className="max-w-3xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-6 text-gray-800">
          Statistiche Studio Pumaisdue
        </h1>
        <div className="grid grid-cols-2 gap-4">
          {cards.map((card, index) => (
            <DashboardCard 
              key={index} 
              {...card} 
              isVisible={visibleCards.includes(index)} 
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default MainView;
