import React from 'react';
import { ArrowLeft, Calendar, TrendingUp, TrendingDown, Activity } from 'lucide-react';
import { getYearlyData } from './data';

const StatCard = ({ icon, label, value, subValue, comparison, backgroundColor }) => (
  <div style={{
    backgroundColor: backgroundColor,
    borderRadius: '12px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    padding: '24px',
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '24px',
  }}>
    <div style={{ display: 'flex', alignItems: 'center', marginBottom: '16px' }}>
      {React.cloneElement(icon, { size: 24, color: '#4B5563' })}
      <span style={{ marginLeft: '12px', fontSize: '18px', fontWeight: '500', color: '#4B5563' }}>{label}</span>
    </div>
    <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px' }}>{value}</div>
    {subValue && <div style={{ fontSize: '14px', color: '#6B7280' }}>{subValue}</div>}
    {comparison && (
      <div style={{ marginTop: '8px', fontSize: '14px' }}>
        <div style={{ color: comparison === 'N/A' ? 'gray' : (parseFloat(comparison) > 0 ? 'green' : 'red') }}>
          {comparison} rispetto all'anno precedente
        </div>
      </div>
    )}
  </div>
);

const calculateComparison = (current, previous) => {
  if (typeof current !== 'number' || typeof previous !== 'number') {
    return 'N/A';
  }
  const diff = current - previous;
  const percentage = (diff / previous) * 100;
  return percentage > 0 ? `+${percentage.toFixed(1)}%` : `${percentage.toFixed(1)}%`;
};

const processYearlyData = (data) => {
  if (!data) return null;

  const months = Object.keys(data);
  const totalTurni = months.reduce((sum, month) => sum + data[month].totaleTurni, 0);

  const monthWithMostTurni = months.reduce((max, month) => 
    data[month].totaleTurni > data[max].totaleTurni ? month : max, months[0]);

  const monthWithLeastTurni = months.reduce((min, month) => 
    data[month].totaleTurni < data[min].totaleTurni ? month : min, months[0]);

  return {
    totalTurni,
    avgTurni: totalTurni / months.length,
    monthWithMostTurni: {
      month: monthWithMostTurni,
      value: data[monthWithMostTurni].totaleTurni
    },
    monthWithLeastTurni: {
      month: monthWithLeastTurni,
      value: data[monthWithLeastTurni].totaleTurni
    }
  };
};

const LastYearView = ({ setView }) => {
  const currentYear = new Date().getFullYear();
  const lastYearData = processYearlyData(getYearlyData(currentYear - 1));
  const previousYearData = processYearlyData(getYearlyData(currentYear - 2));

  if (!lastYearData || !previousYearData) {
    return <div>Error loading data</div>;
  }

  const stats = [
    { 
      icon: <Calendar />, 
      label: 'Totale Turni Annuali', 
      value: lastYearData.totalTurni,
      comparison: calculateComparison(lastYearData.totalTurni, previousYearData.totalTurni),
      backgroundColor: '#E6F3FF'  // Azzurro pastello
    },
    { 
      icon: <Activity />, 
      label: 'Media Turni Mensile', 
      value: lastYearData.avgTurni.toFixed(1),
      comparison: calculateComparison(lastYearData.avgTurni, previousYearData.avgTurni),
      backgroundColor: '#FFF0E6'  // Pesca pastello
    },
    { 
      icon: <TrendingUp />, 
      label: 'Mese con Più Turni', 
      value: `${lastYearData.monthWithMostTurni.value} (${lastYearData.monthWithMostTurni.month})`,
      subValue: `${calculateComparison(lastYearData.monthWithMostTurni.value, previousYearData.monthWithMostTurni.value)} rispetto al mese più alto dell'anno precedente`,
      backgroundColor: '#E6FFE6'  // Verde pastello
    },
    { 
      icon: <TrendingDown />, 
      label: 'Mese con Meno Turni', 
      value: `${lastYearData.monthWithLeastTurni.value} (${lastYearData.monthWithLeastTurni.month})`,
      subValue: `${calculateComparison(lastYearData.monthWithLeastTurni.value, previousYearData.monthWithLeastTurni.value)} rispetto al mese più basso dell'anno precedente`,
      backgroundColor: '#FFE6E6'  // Rosa pastello
    },
  ];

  return (
    <div style={{
      backgroundColor: '#F0F9FF',
      minHeight: '100vh',
      padding: '24px',
      fontFamily: "'Helvetica Neue', Arial, sans-serif",
    }}>
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        <button 
          onClick={() => setView('main')} 
          style={{
            background: '#4B5563',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            padding: '10px 20px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            fontSize: '16px',
            marginBottom: '24px',
            transition: 'background-color 0.3s',
          }}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#374151'}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#4B5563'}
        >
          <ArrowLeft size={20} style={{ marginRight: '8px' }} />
          Torna alla Dashboard
        </button>

        <h1 style={{
          fontSize: '32px',
          fontWeight: 'bold',
          textAlign: 'center',
          marginBottom: '8px',
          color: '#1F2937',
        }}>
          Statistiche Ultimo Anno
        </h1>
        <h2 style={{
          fontSize: '28px',
          fontWeight: 'bold',
          textAlign: 'center',
          marginBottom: '32px',
          color: '#4B5563',
        }}>
          {currentYear - 1}
        </h2>

        <div>
          {stats.map((stat, index) => (
            <StatCard key={index} {...stat} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default LastYearView;
