import React from 'react';
import { getLatestMonthData, getYearlyData, monthlyData } from './data';

const LastMonthView = ({ setView }) => {
    const lastMonthData = getLatestMonthData();
    const yearlyData = getYearlyData();
    const months = Object.keys(monthlyData);
    const previousMonthData = monthlyData[months[months.length - 2]];

    return (
        <div className="space-y-6">
            <button onClick={() => setView('main')} className="mb-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
                Torna alla Dashboard
            </button>
            <h2 className="text-2xl font-bold mb-4">Statistiche Ultimo Mese</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div>
                    <h3>Totale Turni</h3>
                    <p>{lastMonthData.totaleTurni}</p>
                    <p>{((lastMonthData.totaleTurni - previousMonthData.totaleTurni) / previousMonthData.totaleTurni * 100).toFixed(2)}%</p>
                </div>
                <div>
                    <h3>Media Giornaliera</h3>
                    <p>{lastMonthData.mediaGiornaliera.toFixed(2)}</p>
                    <p>{((lastMonthData.mediaGiornaliera - previousMonthData.mediaGiornaliera) / previousMonthData.mediaGiornaliera * 100).toFixed(2)}%</p>
                </div>
                <div>
                    <h3>Massimo Turni</h3>
                    <p>{lastMonthData.massimoTurni.valore} ({lastMonthData.massimoTurni.giorno})</p>
                </div>
                <div>
                    <h3>Minimo Turni</h3>
                    <p>{lastMonthData.minimoTurni.valore} ({lastMonthData.minimoTurni.giorno})</p>
                </div>
            </div>
            <div>
                <h3>Grafico Mensile</h3>
                {/* Qui puoi aggiungere il componente del grafico */}
            </div>
        </div>
    );
};

export default LastMonthView;
