import React, { useState, useEffect } from 'react';
import { ArrowLeft } from 'lucide-react';
import { getMonthData, getAvailableMonths } from './data';

const CompareMonthsView = ({ setView }) => {
  const [selectedMonth1, setSelectedMonth1] = useState('');
  const [selectedMonth2, setSelectedMonth2] = useState('');
  const [comparisonData, setComparisonData] = useState(null);
  const [availableMonths, setAvailableMonths] = useState([]);

  useEffect(() => {
    setAvailableMonths(getAvailableMonths());
  }, []);

  const handleCompare = () => {
    if (selectedMonth1 && selectedMonth2) {
      const data1 = getMonthData(selectedMonth1);
      const data2 = getMonthData(selectedMonth2);

      if (data1 && data2) {
        const comparison = {
          totaleTurni: {
            diff: data2.totaleTurni - data1.totaleTurni,
            percentage: ((data2.totaleTurni - data1.totaleTurni) / data1.totaleTurni * 100).toFixed(2)
          },
          mediaGiornaliera: {
            diff: (data2.mediaGiornaliera - data1.mediaGiornaliera).toFixed(2),
            percentage: ((data2.mediaGiornaliera - data1.mediaGiornaliera) / data1.mediaGiornaliera * 100).toFixed(2)
          },
          massimoTurni: {
            diff: data2.massimoTurni.shifts - data1.massimoTurni.shifts,
            percentage: ((data2.massimoTurni.shifts - data1.massimoTurni.shifts) / data1.massimoTurni.shifts * 100).toFixed(2)
          },
          minimoTurni: {
            diff: data2.minimoTurni.shifts - data1.minimoTurni.shifts,
            percentage: ((data2.minimoTurni.shifts - data1.minimoTurni.shifts) / data1.minimoTurni.shifts * 100).toFixed(2)
          }
        };

        setComparisonData({ month1: data1, month2: data2, comparison });
      }
    }
  };

  const renderComparisonResult = () => {
    if (!comparisonData) return null;

    const { month1, month2, comparison } = comparisonData;
    const getMonthName = (yearMonth) => {
      const [year, month] = yearMonth.split('-');
      return `${availableMonths.find(m => m.value === yearMonth)?.label}`;
    };

    return (
      <div style={{ marginTop: '24px', backgroundColor: 'white', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)' }}>
        <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px', textAlign: 'center' }}>
          Confronto tra {getMonthName(selectedMonth1)} e {getMonthName(selectedMonth2)}
        </h2>
        {Object.entries(comparison).map(([key, value]) => (
          <div key={key} style={{ marginBottom: '16px', padding: '12px', backgroundColor: '#F3F4F6', borderRadius: '4px' }}>
            <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '8px' }}>{key.charAt(0).toUpperCase() + key.slice(1)}</h3>
            <p>
              {getMonthName(selectedMonth1)}: {month1[key].shifts || month1[key].toFixed(2)}
              <br />
              {getMonthName(selectedMonth2)}: {month2[key].shifts || month2[key].toFixed(2)}
              <br />
              Differenza: {value.diff > 0 ? '+' : ''}{value.diff} ({value.percentage}%)
            </p>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div style={{
      backgroundColor: '#F0F9FF',
      minHeight: '100vh',
      padding: '24px',
      fontFamily: "'Helvetica Neue', Arial, sans-serif",
    }}>
      <div style={{ maxWidth: '800px', margin: '0 auto' }}>
        <button 
          onClick={() => setView('main')} 
          style={{
            background: '#4B5563',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            padding: '10px 20px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            fontSize: '16px',
            marginBottom: '24px',
            transition: 'background-color 0.3s',
          }}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#374151'}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#4B5563'}
        >
          <ArrowLeft size={20} style={{ marginRight: '8px' }} />
          Torna alla Dashboard
        </button>

        <h1 style={{
          fontSize: '32px',
          fontWeight: 'bold',
          textAlign: 'center',
          marginBottom: '32px',
          color: '#1F2937',
        }}>
          Confronta Mesi
        </h1>

        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '24px' }}>
          <select 
            value={selectedMonth1} 
            onChange={(e) => setSelectedMonth1(e.target.value)}
            style={{ padding: '8px', fontSize: '16px', borderRadius: '4px', flex: 1, marginRight: '8px' }}
          >
            <option value="">Seleziona il primo mese</option>
            {availableMonths.map(month => (
              <option key={month.value} value={month.value}>{month.label}</option>
            ))}
          </select>
          <select 
            value={selectedMonth2} 
            onChange={(e) => setSelectedMonth2(e.target.value)}
            style={{ padding: '8px', fontSize: '16px', borderRadius: '4px', flex: 1, marginRight: '8px' }}
          >
            <option value="">Seleziona il secondo mese</option>
            {availableMonths.map(month => (
              <option key={month.value} value={month.value}>{month.label}</option>
            ))}
          </select>
          <button 
            onClick={handleCompare}
            style={{
              background: '#3B82F6',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '10px 20px',
              cursor: 'pointer',
              fontSize: '16px',
              transition: 'background-color 0.3s',
            }}
            onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#2563EB'}
            onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#3B82F6'}
          >
            Confronta
          </button>
        </div>

        {renderComparisonResult()}
      </div>
    </div>
  );
};

export default CompareMonthsView;
