import React from 'react';
import { MonthYearIcon, YearIcon, CompareMonthsIcon, CompareYearsIcon, CalendarIcon, PerformanceTrendIcon, InformationIcon } from './Icons';

const DashboardButton = ({ icon, label, color, onClick }) => (
  <div 
    onClick={onClick}
    className={`bg-${color}-100 rounded-xl shadow-md hover:shadow-lg transition-all duration-300 p-4 flex flex-col items-center justify-center aspect-square cursor-pointer hover:bg-${color}-200`}
  >
    <div className="w-2/3 h-2/3 mb-2">{icon}</div>
    <span className="text-sm font-medium text-gray-700 text-center">{label}</span>
  </div>
);

const MainView = ({ setView }) => {
  const buttons = [
    { icon: <MonthYearIcon />, label: "Ultimo Mese", color: "blue", onClick: () => setView('lastMonth') },
    { icon: <YearIcon />, label: "Ultimo Anno", color: "yellow", onClick: () => console.log("Ultimo Anno") },
    { icon: <CompareMonthsIcon />, label: "Confronta mese", color: "green", onClick: () => console.log("Confronta mese") },
    { icon: <CompareYearsIcon />, label: "Confronta Anno", color: "purple", onClick: () => console.log("Confronta Anno") },
    { icon: <CalendarIcon />, label: "Calendario", color: "green", onClick: () => console.log("Calendario") },
    { icon: <PerformanceTrendIcon />, label: "Performance Trend", color: "teal", onClick: () => console.log("Performance Trend") },
    { icon: null, label: "", color: "gray", onClick: () => {} },
    { icon: <InformationIcon />, label: "Informazioni", color: "indigo", onClick: () => console.log("Informazioni") },
  ];

  return (
    <div className="container mx-auto px-4 py-8 bg-light-blue min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-8">Statistiche Studio Pumaisdue</h1>
      <div className="grid grid-cols-2 gap-4 max-w-md mx-auto">
        {buttons.map((button, index) => (
          <DashboardButton key={index} {...button} />
        ))}
      </div>
      <div className="mt-4 text-center text-sm text-gray-600">
        Fai clic per tornare indietro, tieni premuto per vedere la cronologia
      </div>
    </div>
  );
};

export default MainView;
