import React, { useState, useEffect } from 'react';
import { MonthYearIcon, YearIcon, CompareMonthsIcon, CompareYearsIcon, PerformanceTrendIcon, InformationIcon } from './Icons';
import { PlusCircle, Mic } from 'lucide-react';
import StatisticheFonici from './StatisticheFonici';

// Placeholder components for other views
const LastMonth = ({ onBack }) => (
  <div>
    <button onClick={onBack}>Torna indietro</button>
    <h2>Ultimo Mese</h2>
  </div>
);

const LastYear = ({ onBack }) => (
  <div>
    <button onClick={onBack}>Torna indietro</button>
    <h2>Ultimo Anno</h2>
  </div>
);

const CompareMonths = ({ onBack }) => (
  <div>
    <button onClick={onBack}>Torna indietro</button>
    <h2>Confronta Mesi</h2>
  </div>
);

const CompareYears = ({ onBack }) => (
  <div>
    <button onClick={onBack}>Torna indietro</button>
    <h2>Confronta Anni</h2>
  </div>
);

const PerformanceTrend = ({ onBack }) => (
  <div>
    <button onClick={onBack}>Torna indietro</button>
    <h2>Performance Trend</h2>
  </div>
);

const Information = ({ onBack }) => (
  <div>
    <button onClick={onBack}>Torna indietro</button>
    <h2>Informazioni</h2>
  </div>
);

const DataInput = ({ onBack }) => (
  <div>
    <button onClick={onBack}>Torna indietro</button>
    <h2>Inserisci Dati</h2>
  </div>
);

const DashboardCard = ({ icon, label, color, onClick, isVisible }) => {
  const [isPressed, setIsPressed] = useState(false);

  return (
    <div
      onClick={() => {
        onClick();
        setIsPressed(true);
        setTimeout(() => setIsPressed(false), 150);
      }}
      style={{
        backgroundColor: color,
        borderRadius: '12px',
        boxShadow: isPressed 
          ? 'inset 0 2px 4px rgba(0, 0, 0, 0.2)' 
          : '0 4px 6px rgba(0, 0, 0, 0.1)',
        padding: '16px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        aspectRatio: '1 / 1',
        cursor: 'pointer',
        fontFamily: "'Helvetica Neue', Arial, sans-serif",
        transition: 'transform 0.1s, box-shadow 0.1s, opacity 0.2s, scale 0.2s',
        transform: isPressed ? 'scale(0.95)' : 'scale(1)',
        opacity: isVisible ? 1 : 0,
        scale: isVisible ? 1 : 0.8,
      }}
      onMouseEnter={(e) => {
        if (!isPressed) {
          e.currentTarget.style.transform = 'scale(1.05)';
          e.currentTarget.style.boxShadow = '0 6px 8px rgba(0, 0, 0, 0.15)';
        }
      }}
      onMouseLeave={(e) => {
        if (!isPressed) {
          e.currentTarget.style.transform = 'scale(1)';
          e.currentTarget.style.boxShadow = '0 4px 6px rgba(0, 0, 0, 0.1)';
        }
      }}
    >
      <div style={{ width: '75%', height: '75%', marginBottom: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>{icon}</div>
      <span style={{ fontSize: '14px', fontWeight: '500', color: '#4B5563', textAlign: 'center' }}>{label}</span>
    </div>
  );
};

const MainView = () => {
  const [visibleCards, setVisibleCards] = useState([]);
  const [currentView, setCurrentView] = useState('main');

  const cards = [
    { icon: <MonthYearIcon />, label: "Ultimo Mese", color: "#E6F3FF", onClick: () => setCurrentView('lastMonth') },
    { icon: <YearIcon />, label: "Ultimo Anno", color: "#FEF3C7", onClick: () => setCurrentView('lastYear') },
    { icon: <CompareMonthsIcon />, label: "Confronta mesi", color: "#D1FAE5", onClick: () => setCurrentView('compareMonths') },
    { icon: <CompareYearsIcon />, label: "Confronta Anno", color: "#EDE9FE", onClick: () => setCurrentView('compareYears') },
    { icon: <Mic size={48} />, label: "Statistiche Fonici", color: "#FEE2E2", onClick: () => setCurrentView('statisticheFonici') },
    { icon: <PerformanceTrendIcon />, label: "Performance Trend", color: "#D1FAE5", onClick: () => setCurrentView('performanceTrend') },
    { icon: <InformationIcon />, label: "Informazioni", color: "#E0E7FF", onClick: () => setCurrentView('information') },
    { icon: <PlusCircle size={48} />, label: "Inserisci Dati", color: "#FDE68A", onClick: () => setCurrentView('dataInput') },
  ];

  useEffect(() => {
    const totalAnimationTime = 1000; // 1 second
    const intervalTime = totalAnimationTime / cards.length;

    cards.forEach((_, index) => {
      setTimeout(() => {
        setVisibleCards(prev => {
          const newIndexes = [...prev, index];
          // Shuffle the new indexes to add randomness
          return newIndexes.sort(() => Math.random() - 0.5);
        });
      }, index * intervalTime);
    });
  }, []);

  const renderView = () => {
    switch(currentView) {
      case 'lastMonth':
        return <LastMonth onBack={() => setCurrentView('main')} />;
      case 'lastYear':
        return <LastYear onBack={() => setCurrentView('main')} />;
      case 'compareMonths':
        return <CompareMonths onBack={() => setCurrentView('main')} />;
      case 'compareYears':
        return <CompareYears onBack={() => setCurrentView('main')} />;
      case 'statisticheFonici':
        return <StatisticheFonici onBack={() => setCurrentView('main')} />;
      case 'performanceTrend':
        return <PerformanceTrend onBack={() => setCurrentView('main')} />;
      case 'information':
        return <Information onBack={() => setCurrentView('main')} />;
      case 'dataInput':
        return <DataInput onBack={() => setCurrentView('main')} />;
      default:
        return (
          <div style={{
            backgroundColor: '#F0F9FF',
            minHeight: '100vh',
            padding: '16px',
            fontFamily: "'Helvetica Neue', Arial, sans-serif",
          }}>
            <div style={{ maxWidth: '768px', margin: '0 auto' }}>
              <h1 style={{
                fontSize: '28px',
                fontWeight: 'bold',
                textAlign: 'center',
                marginBottom: '24px',
                color: '#2D3748',
              }}>
                Statistiche Studio Pumaisdue
              </h1>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '16px' }}>
                {cards.map((card, index) => (
                  <DashboardCard key={index} {...card} isVisible={visibleCards.includes(index)} />
                ))}
              </div>
            </div>
          </div>
        );
    }
  };

  return renderView();
};

export default MainView;
