import React from 'react';
import { ArrowLeft, Calendar, Clock, TrendingUpDown } from 'lucide-react';
import { getLatestMonthData, getPreviousMonthData, getAnnualAverageData } from './data';

const StatCard = ({ icon, label, value, subValue, comparison, backgroundColor, maxMin }) => (
  <div style={{
    backgroundColor: backgroundColor,
    borderRadius: '12px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    padding: '24px',
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '24px',
  }}>
    <div style={{ display: 'flex', alignItems: 'center', marginBottom: '16px' }}>
      {React.cloneElement(icon, { size: 24, color: '#4B5563' })}
      <span style={{ marginLeft: '12px', fontSize: '18px', fontWeight: '500', color: '#4B5563' }}>{label}</span>
    </div>
    {maxMin ? (
      <div>
        <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px' }}>
          Max: {maxMin.max.valore} (giorno {maxMin.max.giorno})
        </div>
        <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px' }}>
          Min: {maxMin.min.valore} (giorno {maxMin.min.giorno})
        </div>
      </div>
    ) : (
      <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px' }}>{value}</div>
    )}
    {subValue && <div style={{ fontSize: '14px', color: '#6B7280' }}>{subValue}</div>}
    {comparison && (
      <div style={{ marginTop: '8px', fontSize: '14px' }}>
        <div style={{ color: comparison.prevMonth.value === 'N/A' ? 'gray' : (parseFloat(comparison.prevMonth.value) > 0 ? 'green' : 'red') }}>
          {comparison.prevMonth.value} ({comparison.prevMonth.percentage}) rispetto a {comparison.prevMonthName}
        </div>
        <div style={{ color: comparison.annual.value === 'N/A' ? 'gray' : (parseFloat(comparison.annual.value) > 0 ? 'green' : 'red') }}>
          {comparison.annual.value} ({comparison.annual.percentage}) rispetto alla media annuale {comparison.year}
        </div>
      </div>
    )}
  </div>
);

const formatDate = (day) => {
  return day;  // Restituisce solo il giorno
};

const getPreviousMonth = (date) => {
  const prevMonth = new Date(date.getFullYear(), date.getMonth() - 1, 1);
  const monthNames = ['Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno', 'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'];
  return {
    name: `${monthNames[prevMonth.getMonth()]} ${prevMonth.getFullYear()}`,
    month: monthNames[prevMonth.getMonth()],
    year: prevMonth.getFullYear()
  };
};

const calculateComparison = (current, previous) => {
  if (typeof current !== 'number' || typeof previous !== 'number') {
    return { value: 'N/A', percentage: 'N/A' };
  }
  const diff = current - previous;
  const percentage = ((diff / previous) * 100).toFixed(1);
  return {
    value: diff > 0 ? `+${diff.toFixed(1)}` : diff.toFixed(1),
    percentage: diff > 0 ? `+${percentage}%` : `${percentage}%`
  };
};

const LastMonthView = ({ setView }) => {
  const currentDate = new Date();
  const lastMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1);
  const latestMonthData = getLatestMonthData();
  const previousMonthData = getPreviousMonthData();
  const annualAverageData = getAnnualAverageData();

  const monthNames = ['GENNAIO', 'FEBBRAIO', 'MARZO', 'APRILE', 'MAGGIO', 'GIUGNO', 'LUGLIO', 'AGOSTO', 'SETTEMBRE', 'OTTOBRE', 'NOVEMBRE', 'DICEMBRE'];
  const displayMonth = monthNames[lastMonth.getMonth()];
  const displayYear = lastMonth.getFullYear();

  const previousMonthInfo = getPreviousMonth(lastMonth);

  const createComparisonData = (current, previous, annualAvg) => ({
    prevMonth: calculateComparison(current, previous),
    annual: calculateComparison(current, annualAvg),
    prevMonthName: previousMonthInfo.name,
    year: lastMonth.getFullYear()
  });

  const comparisonDataTurni = createComparisonData(
    latestMonthData.totaleTurni,
    previousMonthData.totaleTurni,
    annualAverageData.mediaAnnuale
  );

  const comparisonDataMedia = createComparisonData(
    latestMonthData.mediaGiornaliera,
    previousMonthData.mediaGiornaliera,
    annualAverageData.mediaAnnuale
  );

  const stats = [
    { 
      icon: <Calendar />, 
      label: 'Totale Turni', 
      value: latestMonthData.totaleTurni,
      comparison: comparisonDataTurni,
      backgroundColor: '#E6F3FF'  // Azzurro pastello
    },
    { 
      icon: <Clock />, 
      label: 'Media Turni Giornaliera', 
      value: latestMonthData.mediaGiornaliera.toFixed(1),
      comparison: comparisonDataMedia,
      backgroundColor: '#FFF0E6'  // Pesca pastello
    },
    { 
      icon: <TrendingUpDown />, 
      label: 'Massimo e Minimo Turni', 
      maxMin: {
        max: {
          valore: latestMonthData.massimoTurni.valore,
          giorno: formatDate(latestMonthData.massimoTurni.giorno)
        },
        min: {
          valore: latestMonthData.minimoTurni.valore,
          giorno: formatDate(latestMonthData.minimoTurni.giorno)
        }
      },
      backgroundColor: '#E6FFE6'  // Verde pastello
    },
  ];

  return (
    <div style={{
      backgroundColor: '#F0F9FF',
      minHeight: '100vh',
      padding: '24px',
      fontFamily: "'Helvetica Neue', Arial, sans-serif",
    }}>
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        <button 
          onClick={() => setView('main')} 
          style={{
            background: '#4B5563',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            padding: '10px 20px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            fontSize: '16px',
            marginBottom: '24px',
            transition: 'background-color 0.3s',
          }}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#374151'}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#4B5563'}
        >
          <ArrowLeft size={20} style={{ marginRight: '8px' }} />
          Torna alla Dashboard
        </button>

        <h1 style={{
          fontSize: '32px',
          fontWeight: 'bold',
          textAlign: 'center',
          marginBottom: '8px',
          color: '#1F2937',
        }}>
          Statistiche Ultimo Mese
        </h1>
        <h2 style={{
          fontSize: '28px',
          fontWeight: 'bold',
          textAlign: 'center',
          marginBottom: '32px',
          color: '#4B5563',
          textTransform: 'uppercase',
        }}>
          {displayMonth} {displayYear}
        </h2>

        <div>
          {stats.map((stat, index) => (
            <StatCard key={index} {...stat} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default LastMonthView;
