import React from 'react';
import { ArrowLeft, Calendar, Clock, TrendingUpDown } from 'lucide-react';
import { getLatestMonthData, getPreviousMonthData, getAnnualAverageData } from './data';

const StatCard = ({ icon, label, value, subValue, comparison }) => (
  <div style={{
    backgroundColor: 'white',
    borderRadius: '12px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    padding: '24px',
    display: 'flex',
    flexDirection: 'column',
    height: '100%',
  }}>
    <div style={{ display: 'flex', alignItems: 'center', marginBottom: '16px' }}>
      {React.cloneElement(icon, { size: 24, color: '#4B5563' })}
      <span style={{ marginLeft: '12px', fontSize: '16px', fontWeight: '500', color: '#4B5563' }}>{label}</span>
    </div>
    <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#1F2937', marginBottom: '8px' }}>{value}</div>
    {subValue && <div style={{ fontSize: '14px', color: '#6B7280' }}>{subValue}</div>}
    {comparison && (
      <div style={{ marginTop: '8px', fontSize: '14px' }}>
        <div style={{ color: comparison.prevMonth === 'N/A' ? 'gray' : (parseFloat(comparison.prevMonth) > 0 ? 'green' : 'red') }}>
          {comparison.prevMonth} rispetto al mese precedente
        </div>
        <div style={{ color: comparison.annual === 'N/A' ? 'gray' : (parseFloat(comparison.annual) > 0 ? 'green' : 'red') }}>
          {comparison.annual} rispetto alla media annuale
        </div>
      </div>
    )}
  </div>
);

const InfoBox = ({ title, items }) => (
  <div style={{ 
    backgroundColor: 'white', 
    borderRadius: '12px', 
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', 
    padding: '24px',
    height: '100%',
  }}>
    <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '16px', color: '#1F2937' }}>{title}</h3>
    <ul style={{ listStyleType: 'none', padding: 0 }}>
      {items.map((item, index) => (
        <li key={index} style={{ marginBottom: '12px' }}>
          <span style={{ fontWeight: '500', color: '#4B5563' }}>{item.label}:</span>
          <span style={{ marginLeft: '8px', color: '#1F2937' }}>{item.value}</span>
        </li>
      ))}
    </ul>
  </div>
);

const formatDate = (day, month) => {
  const months = ['Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno', 'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'];
  const monthIndex = months.indexOf(month.split(' ')[0]);
  const date = new Date(new Date().getFullYear(), monthIndex, parseInt(day));
  return date.toLocaleDateString('it-IT', { day: 'numeric', month: 'long' });
};

const getPreviousMonth = () => {
  const now = new Date();
  const prevMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
  const monthNames = ['Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno', 'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'];
  return `${monthNames[prevMonth.getMonth()]} ${prevMonth.getFullYear()}`;
};

const calculateComparison = (current, previous) => {
  if (typeof current !== 'number' || typeof previous !== 'number') {
    return 'N/A';
  }
  const diff = current - previous;
  return diff > 0 ? `+${diff}` : diff.toString();
};

const LastMonthView = ({ setView }) => {
  const latestMonthData = getLatestMonthData();
  const previousMonthData = getPreviousMonthData();
  const annualAverageData = getAnnualAverageData();
  const latestMonth = Object.keys(latestMonthData)[0];
  const previousMonthDisplay = getPreviousMonth();

  const comparisonData = {
    prevMonth: calculateComparison(latestMonthData.totaleTurni, previousMonthData.totaleTurni),
    annual: calculateComparison(latestMonthData.totaleTurni, annualAverageData.mediaAnnuale)
  };

  const stats = [
    { 
      icon: <Calendar />, 
      label: 'Totale Turni', 
      value: latestMonthData.totaleTurni,
      comparison: comparisonData
    },
    { icon: <Clock />, label: 'Media Giornaliera', value: latestMonthData.mediaGiornaliera.toFixed(1) },
    { 
      icon: <TrendingUpDown />, 
      label: 'Massimo e Minimo Turni', 
      value: `${latestMonthData.massimoTurni.valore} | ${latestMonthData.minimoTurni.valore}`,
      subValue: `Max: ${formatDate(latestMonthData.massimoTurni.giorno, latestMonth)} - Min: ${formatDate(latestMonthData.minimoTurni.giorno, latestMonth)}`
    },
  ];

  const monthInfo = [
    { label: 'Mese e Anno', value: latestMonth },
    { label: 'Turni Totali', value: latestMonthData.totaleTurni },
    { label: 'Media Giornaliera', value: latestMonthData.mediaGiornaliera.toFixed(1) },
    { label: 'Giorno con Più Turni', value: `${formatDate(latestMonthData.massimoTurni.giorno, latestMonth)} (${latestMonthData.massimoTurni.valore} turni)` },
    { label: 'Giorno con Meno Turni', value: `${formatDate(latestMonthData.minimoTurni.giorno, latestMonth)} (${latestMonthData.minimoTurni.valore} turni)` },
  ];

  return (
    <div style={{
      backgroundColor: '#F0F9FF',
      minHeight: '100vh',
      padding: '24px',
      fontFamily: "'Helvetica Neue', Arial, sans-serif",
    }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <button 
          onClick={() => setView('main')} 
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            fontSize: '16px',
            color: '#4B5563',
            marginBottom: '24px',
          }}
        >
          <ArrowLeft size={20} style={{ marginRight: '8px' }} />
          Torna alla Dashboard
        </button>

        <h1 style={{
          fontSize: '32px',
          fontWeight: 'bold',
          textAlign: 'center',
          marginBottom: '8px',
          color: '#1F2937',
        }}>
          Statistiche ultimo mese
        </h1>
        <h2 style={{
          fontSize: '24px',
          fontWeight: 'normal',
          textAlign: 'center',
          marginBottom: '32px',
          color: '#4B5563',
        }}>
          {previousMonthDisplay}
        </h2>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '24px', marginBottom: '32px' }}>
          {stats.map((stat, index) => (
            <StatCard key={index} {...stat} />
          ))}
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '24px' }}>
          <InfoBox title="Riepilogo del Mese" items={monthInfo} />
          <div style={{ 
            backgroundColor: 'white', 
            borderRadius: '12px', 
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', 
            padding: '24px',
            height: '100%',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: '18px',
            color: '#4B5563'
          }}>
            Grafico Andamento Turni
          </div>
        </div>
      </div>
    </div>
  );
};

export default LastMonthView;
