import React from 'react';
import { StatsCard, Chart } from './Components';
import { data } from './data';

const LastMonthView = ({ setView }) => {
    const lastMonthData = data[Object.keys(data)[Object.keys(data).length - 1]];
    const previousMonthData = data[Object.keys(data)[Object.keys(data).length - 2]];

    return (
        <div className="space-y-6">
            <button onClick={() => setView('main')} className="mb-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
                Torna alla Dashboard
            </button>
            <h2 className="text-2xl font-bold mb-4">Statistiche Ultimo Mese</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <StatsCard 
                    title="Totale Turni" 
                    value={lastMonthData.totaleTurni}
                    comparison={((lastMonthData.totaleTurni - previousMonthData.totaleTurni) / previousMonthData.totaleTurni * 100)}
                />
                <StatsCard 
                    title="Media Giornaliera" 
                    value={lastMonthData.mediaGiornaliera.toFixed(2)}
                    comparison={((lastMonthData.mediaGiornaliera - previousMonthData.mediaGiornaliera) / previousMonthData.mediaGiornaliera * 100)}
                />
                <StatsCard 
                    title="Massimo Turni" 
                    value={`${lastMonthData.massimoTurni.valore} (${lastMonthData.massimoTurni.giorno})`}
                />
                <StatsCard 
                    title="Minimo Turni" 
                    value={`${lastMonthData.minimoTurni.valore} (${lastMonthData.minimoTurni.giorno})`}
                />
            </div>
            <div className="bg-white p-4 rounded-lg shadow">
                <h3 className="text-lg font-semibold mb-4">Grafico Mensile</h3>
                <Chart data={{ [Object.keys(data)[Object.keys(data).length - 1]]: lastMonthData }} />
            </div>
        </div>
    );
};

export default LastMonthView;
